mapper.select:
SELECT r1.id, r1.role_name, r1.role_code, r1.role_sort, r1.status, r1.remark, r1.create_by, r1.create_time, r1.update_by, r1.update_time, r1.is_deleted, GROUP_CONCAT(DISTINCT r3.id) AS ids, GROUP_CONCAT(DISTINCT r3.menu_name) AS menu_names FROM study_java_sys_role r1 LEFT JOIN study_java_sys_role_menus m ON r1.id = m.role_id LEFT JOIN study_java_sys_menu r3 ON r3.id = m.menu_id
r1.is_deleted = 0
AND r1.role_name LIKE CONCAT('%', #{studyJavaSysRoleDao.roleName},'%')
AND r1.role_code = #{studyJavaSysRoleDao.roleCode}
AND r1.status = #{studyJavaSysRoleDao.status}
GROUP BY r1.id ORDER BY r1.role_sort ASC

mapper.update:
UPDATE study_java_sys_role
role_name = #{studyJavaSysRoleDao.roleName}, role_code = #{studyJavaSysRoleDao.roleCode}, role_sort = #{studyJavaSysRoleDao.roleSort}, status = #{studyJavaSysRoleDao.status}, remark = #{studyJavaSysRoleDao.remark}, update_by = #{studyJavaSysRoleDao.updateBy}, update_time = #{studyJavaSysRoleDao.updateTime},
WHERE id = #{studyJavaSysRoleDao.id}

UPDATE study_java_sys_role
status = #{studyJavaSysRoleDao.status}, update_by = #{studyJavaSysRoleDao.updateBy}, update_time = #{studyJavaSysRoleDao.updateTime},
WHERE id = #{studyJavaSysRoleDao.id}

UPDATE study_java_sys_role
SET is_deleted = 1
WHERE id = #{roleId}

