mapper.select:
SELECT r1.id, r1.role_name, r1.role_code, r1.role_sort, r1.status, r1.remark, r1.create_by, r1.create_time, r1.update_by, r1.update_time, r1.del_flag, GROUP_CONCAT(DISTINCT r3.id) AS ids, GROUP_CONCAT(DISTINCT r3.menu_name) AS menu_names FROM study_java_sys_role r1 LEFT JOIN study_java_sys_role_menus m ON r1.id = m.role_id LEFT JOIN study_java_sys_menu r3 ON r3.id = m.menu_id
del_flag = 0
AND role_name LIKE CONCAT('%', #{studyJavaSysRoleDao.roleName},'%')
AND role_code = #{studyJavaSysRoleDao.roleCode}
AND status = #{studyJavaSysRoleDao.status}
GROUP BY r1.id ORDER BY r1.role_sort ASC

mapper.update:
UPDATE study_java_sys_role
role_name = #{roleDao.roleName}, role_code = #{roleDao.roleCode}, role_sort = #{roleDao.roleSort}, status = #{roleDao.status}, remark = #{roleDao.remark}, update_by = #{roleDao.updateBy}, update_time = #{roleDao.updateTime},
WHERE id = #{roleDao.id}

UPDATE study_java_sys_role
status = #{roleDao.status}, update_by = #{roleDao.updateBy}, update_time = #{roleDao.updateTime},
WHERE id = #{roleDao.id}

UPDATE study_java_sys_role
SET del_flag = 1
WHERE id = #{roleId}

